        if ($("#loginUsername").val().length == 0) {
            if (window.device) {
                navigator.notification.alert("Please enter your username.", null, 'Login', 'Ok');
            } else {
                alert('Please enter your username.');
            }
            return false;
        }
        if ($("#loginPassword").val().length == 0) {
