function pageprocessorMyRewards(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    var tenantid = $rootScope.metadata.tenantid;
    var mobappid = $rootScope.metadata.mobappid;
    var featureid = "";
    var myfeatureid = "";
    $scope.mypoints = 0;
    $.each($rootScope.metadata.apps, function(i, val) {
        if (val.name === "RestoOrder") {
            featureid = val.id;
        }
        if (val.name === $routeParams.appid) {
            myfeatureid = val.id;
        }
    });
    var dsbaseurl = "https://kryptosda.kryptosmobile.com/kryptosds/";
    var myprofile = $.jStorage.get($rootScope.metadata.tenantid + 'profile');
    if (myprofile) {
        $scope.myprofile = myprofile;
    } else {
        $location.path("/app/MyAppProfile/MyAppProfilePage2");
        return;
    }
    if (myprofile) {
        $.blockUI();
        var url = dsbaseurl + "api/queryTenantData/" + tenantid + "/" + mobappid + "/" + featureid + "?subFeature=OnlineOrders";
        var proxyMethod = "POST";
        var proxyData = {
            "Email": myprofile.email
        };
        $http({
            method: proxyMethod,
            url: url,
            data: proxyData
        }).
        success(function(data, status, headers, config) {
            $.unblockUI();
            console.log(data);
            var point = 0
            $.each(data, function(i, val) {
                point += val.TotalAmount
            });
            $scope.mypoints = Math.round(point);
            $scope.myorders = data;
        }).
        error(function(data, status, headers, config) {
            $.unblockUI();

        });
    }
}

